import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import 'bootstrap/dist/css/bootstrap.min.css';

function ProductDetails() {
    const [title, setTitle] = useState('');
    const [image, setImage] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [isEditMode, setIsEditMode] = useState(false);  // State to determine mode (Add/Edit)
    const { id } = useParams();
    const navigate = useNavigate();

    // Fetch product details if in edit mode
    useEffect(() => {
        if (id) {
            fetch(`http://localhost:3000/products/${id}`)
                .then((res) => res.json())
                .then((data) => {
                    setImage(data.image);
                    setTitle(data.title);
                    setDescription(data.description);
                    setPrice(data.price);
                    setIsEditMode(true);  // Set edit mode
                })
                .catch((error) => console.error('Error fetching product:', error));
        } else {
            setIsEditMode(false);  // Set add mode if no id
        }
    }, [id]);

    function submit(e) {
        e.preventDefault();

        const url = isEditMode ? `http://localhost:3000/products/${id}` : `http://localhost:3000/products`;
        const method = isEditMode ? 'PUT' : 'POST';

        fetch(url, {
            method: method,
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                title: title,
                image: image,
                description: description,
                price: price,
            }),
        })
            .then((res) => res.json())
            .then(() => {
                const successMessage = isEditMode ? 'Product updated successfully!' : 'Product added successfully!';
                toast.success(successMessage);
                if (!isEditMode) {
                    // Reset form if adding a new product
                    setTitle('');
                    setImage('');
                    setDescription('');
                    setPrice('');
                }
                if (isEditMode) {
                    navigate('/');
                }
            })
            .catch((error) => {
                console.error('Error submitting product:', error);
                toast.error('Failed to submit product.');
            });
    }

    function Delete() {
        fetch(`http://localhost:3000/products/${id}`, {
            method: 'DELETE',
        })
            .then((res) => res.json())
            .then(() => {
                console.log('Product deleted');
                navigate('/');
                toast.success('Deleted successfully');
            });
    }

    return (
        <div className="container mt-4">
            <div className="row">
                <div className="col-md-6 text-center">
                    <img 
                        src={image} 
                        alt={title} 
                        className="img-fluid" 
                        style={{ maxHeight: '400px', objectFit: 'contain' }} 
                    />
                </div>

                <div className="col-md-6">
                    <h1 className="mb-3">{title}</h1>
                    <h3 className="text-primary mb-3">Price: ${price}</h3>
                    <p className="mb-4">{description}</p>

                    {id && (
                        <button onClick={Delete} type="button" className="btn mt-3 btn-danger btn-sm">
                            DELETE
                        </button>
                    )}

                    <button 
                        type="button" 
                        className="btn mt-3 btn-primary btn-sm ms-2" 
                        data-bs-toggle="modal" 
                        data-bs-target="#addUpdateProductModal"
                        onClick={() => {
                            if (!id) setIsEditMode(false);  // Set add mode if no ID
                        }}
                    >
                        {isEditMode ? "Update Product" : "Add Product"}
                    </button>
                </div>
            </div>

            {/* Modal for Add/Update Product */}
            <div
                className="modal fade"
                id="addUpdateProductModal"
                tabIndex="-1"
                aria-labelledby="addUpdateProductModalLabel"
                aria-hidden="true"
            >
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="addUpdateProductModalLabel">
                                {isEditMode ? 'Update Product' : 'Add Product'}
                            </h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={submit}>
                                <div className="mb-3">
                                    <label htmlFor="title" className="form-label">Title</label>
                                    <input
                                        type="text"
                                        id="title"
                                        className="form-control"
                                        placeholder="Enter product title"
                                        value={title}
                                        onChange={(e) => setTitle(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="image" className="form-label">Image URL</label>
                                    <input
                                        type="text"
                                        id="image"
                                        className="form-control"
                                        placeholder="Enter image URL"
                                        value={image}
                                        onChange={(e) => setImage(e.target.value)}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="description" className="form-label">Description</label>
                                    <textarea
                                        id="description"
                                        className="form-control"
                                        placeholder="Enter product description"
                                        value={description}
                                        onChange={(e) => setDescription(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="price" className="form-label">Price</label>
                                    <input
                                        type="number"
                                        id="price"
                                        className="form-control"
                                        placeholder="Enter product price"
                                        value={price}
                                        onChange={(e) => setPrice(e.target.value)}
                                        required
                                    />
                                </div>
                                <button type="submit" className="btn btn-primary w-100" data-bs-dismiss="modal">
                                    {isEditMode ? 'Update Product' : 'Add Product'}
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ProductDetails;
