import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

function ProductDetails() {
    const [title, setTitle] = useState('');
    const [image, setImage] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [quantity, setQuantity] = useState(0);
    const [relatedProducts, setRelatedProducts] = useState([]);
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch the selected product
        fetch(`http://localhost:3000/products/${id}`)
            .then((res) => res.json())
            .then((data) => {
                setImage(data.image);
                setTitle(data.title);
                setDescription(data.description);
                setPrice(data.price);
                setQuantity(data.quantity);
            })
            .catch((error) => console.error('Error fetching product:', error));

        // Fetch related products
        fetch(`http://localhost:3000/products`)
            .then((res) => res.json())
            .then((data) => {
                // Filter out the current product
                const filteredProducts = data.filter(product => product.id !== parseInt(id));
                setRelatedProducts(filteredProducts);
            })
            .catch((error) => console.error('Error fetching related products:', error));
    }, [id]);

    function handleDelete() {
        fetch(`http://localhost:3000/products/${id}`, {
            method: 'DELETE',
        })
            .then(() => {
                toast.success('Deleted successfully');
                navigate('/');
            })
            .catch((error) => console.error('Error deleting product:', error));
    }

    function handleUpdate(e) {
        e.preventDefault();
        fetch(`http://localhost:3000/products/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                title,
                image,
                description,
                price,
                quantity,
            }),
        })
            .then(() => {
                toast.success('Product updated successfully!');
            })
            .catch((error) => {
                console.error('Error updating product:', error);
                toast.error('Failed to update product.');
            });
    }

    return (
        <div className="container mt-4">
            <div className="row">
                <div className="col-md-6 text-center">
                    <img 
                        src={image} 
                        alt={title} 
                        className="img-fluid" 
                        style={{ maxHeight: '400px', objectFit: 'contain' }} 
                    />
                </div>
                <div className="col-md-6">
                    <h1>{title}</h1>
                    <h3 className="text-primary">Price: ${price}</h3>
                    <p>{description}</p>
                    <p>Quantity: {quantity}</p>
                    <button onClick={handleDelete} className="btn btn-danger btn-sm">DELETE</button>
                    <button 
                        className="btn btn-warning btn-sm ms-2" 
                        data-bs-toggle="modal" 
                        data-bs-target="#updateProductModal"
                    >
                        Update
                    </button>
                </div>
            </div>

            {/* Related Products Section */}
            <h4 className="mt-5">Related Products</h4>
            <div className="row">
                {relatedProducts.map((product) => (
                    <div key={product.id} className="col-md-3 mb-4">
                        <div className="card h-100">
                            <img src={product.image} className="card-img-top" alt={product.title} style={{ maxHeight: '150px', objectFit: 'contain' }} />
                            <div className="card-body">
                                <h5 className="card-title">{product.title}</h5>
                                <p className="card-text">KES {product.price}</p>
                                <button 
                                    onClick={() => navigate(`/products/${product.id}`)}
                                    className="btn btn-primary btn-sm"
                                >
                                    View Details
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Update Product Modal */}
            <div
                className="modal fade"
                id="updateProductModal"
                tabIndex="-1"
                aria-labelledby="updateProductModalLabel"
                aria-hidden="true"
            >
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Update Product</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={handleUpdate}>
                                <div className="mb-3">
                                    <label>Title</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={title}
                                        onChange={(e) => setTitle(e.target.value)}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label>Image URL</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={image}
                                        onChange={(e) => setImage(e.target.value)}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label>Description</label>
                                    <textarea
                                        className="form-control"
                                        value={description}
                                        onChange={(e) => setDescription(e.target.value)}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label>Price</label>
                                    <input
                                        type="number"
                                        className="form-control"
                                        value={price}
                                        onChange={(e) => setPrice(e.target.value)}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label>Quantity</label>
                                    <input
                                        type="number"
                                        className="form-control"
                                        value={quantity}
                                        onChange={(e) => setQuantity(e.target.value)}
                                    />
                                </div>
                                <button type="submit" className="btn btn-primary">Update Product</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ProductDetails;
