import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import AddProduct from './Addproduct';
import ElectronicsSlider from './ElectronicsSlider';
import { FaShoppingCart } from 'react-icons/fa';


function Home() {
    const [products, setProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true); // Track loading state
    const [cart, setCart] = useState([]);

    // Fetch products on load
    useEffect(() => {
        fetch("https://electric-app.onrender.com/products")
            .then((response) => response.json())
            .then((data) => {
                setProducts(data);
                setIsLoading(false); // Stop loading after fetching
            })
            .catch((error) => {
                console.error("Error fetching products", error);
                setIsLoading(false);
            });
    }, []);

    // Load cart from localStorage if it exists
    useEffect(() => {
        const savedCart = JSON.parse(localStorage.getItem("cart"));
        if (savedCart) {
            setCart(savedCart);
        }
    }, []);

    // Save cart to localStorage whenever it changes
    useEffect(() => {
        localStorage.setItem("cart", JSON.stringify(cart));
    }, [cart]);

    // Function to add a product to the cart
    const addToCart = (product) => {
        setCart([...cart, product]);
    };

    // Conditionally render loader or product list
    return (
        <>
            <ElectronicsSlider />
            <AddProduct />
            <div className="container mt-4">
                <h1 className="text-center mb-4">Products</h1>

                {isLoading ? (
                    <div className="loader">
                        <div className="truckWrapper">
                            <div className="truckBody">
                                {/* SVG content here */}
                            </div>
                            <div className="truckTires">
                                {/* Tire SVG content */}
                            </div>
                            <div className="road"></div>
                            <svg /* Lamp post SVG */></svg>
                        </div>
                    </div>
                ) : (
                    <>
                        {/* Display product cards when not loading */}
                        <div className="row">
                            {products.map(product => (
                                <div className="col-md-3 mb-4" key={product.id}>
                                    <div className="card h-100 product-card">
                                        <Link
                                            to={`/Productdetails/${product.id}`}
                                            style={{ textDecoration: 'none', color: 'inherit' }}
                                        >
                                            <img
                                                src={product.image}
                                                className="card-img-top"
                                                alt={product.title}
                                                style={{ height: '200px', objectFit: 'cover' }}
                                            />
                                            <div className="card-body">
                                                <h5 className="card-title">{product.title}</h5>
                                            </div>
                                            <h5 className="card-title" style={{ color: 'red' }}>Ksh:{product.price}</h5>
                                        </Link>
                                        <button
                                            className="btn add-to-cart-btn"
                                            onClick={() => addToCart(product)}
                                        >
                                            <FaShoppingCart />
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </>
                )}
            </div>
        </>
    );
}

export default Home;
