import React, { useState } from 'react';
import './Feedback.css';

const Feedback = () => {
    const [like, setLike] = useState(false);
    const [dislike, setDislike] = useState(false);

    const handleLike = () => {
        setLike(!like);
        if (dislike) setDislike(false); // Ensure like and dislike are not both selected
    };

    const handleDislike = () => {
        setDislike(!dislike);
        if (like) setLike(false); // Ensure like and dislike are not both selected
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission logic here
        console.log("Like:", like, "Dislike:", dislike);
    };

    return (
        <div className="container">
            <h2>Feedback</h2>
            <form onSubmit={handleSubmit}>
                <div className="like-dislike-container">
                    <div className="icons-box">
                        <div className="btn-label" onClick={handleLike}>
                            <input type="checkbox" className="input-box" checked={like} readOnly />
                            <span className="svgs">üëç</span>
                            <span className="text-content">Like</span>
                        </div>
                        <div className="btn-label" onClick={handleDislike}>
                            <input type="checkbox" className="input-box" checked={dislike} readOnly />
                            <span className="svgs">üëé</span>
                            <span className="text-content">Dislike</span>
                        </div>
                    </div>
                </div>
                <button type="submit" className="btn-primary">Submit Feedback</button>
            </form>
        </div>
    );
};

export default Feedback;
