import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

function ProductDetails() {
    const [title, setTitle] = useState('');
    const [image, setImage] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const { id } = useParams();
    const navigate = useNavigate();
    const [quantity, setQuantity] = useState(0);
    const [relatedProducts, setRelatedProducts] = useState([]);

    useEffect(() => {
        // Fetch main product details
        fetch(`http://localhost:3000/products/${id}`)
            .then((res) => res.json())
            .then((data) => {
                setImage(data.image);
                setTitle(data.title);
                setDescription(data.description);
                setPrice(data.price);
                setQuantity(data.Quantity);

                // Fetch related products
                return fetch('http://localhost:3000/products'); // Replace with your related products API endpoint
            })
            .then((res) => res.json())
            .then((relatedData) => {
                setRelatedProducts(relatedData); // Set related products
            })
            .catch((error) => console.error('Error fetching product:', error));
    }, [id]);

    function Delete() {
        fetch(`http://localhost:3000/products/${id}`, {
            method: 'DELETE',
        })
            .then((res) => res.json())
            .then(() => {
                console.log('Product deleted');
                navigate('/');
                toast.success('Deleted successfully');
            });
    }

    function submit(e) {
        e.preventDefault();

        fetch(`http://localhost:3000/products/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                title,
                image,
                description,
                price,
                Quantity: quantity,
            }),
        })
            .then((res) => res.json())
            .then(() => {
                toast.success('Product updated successfully!');
            })
            .catch((error) => {
                console.error('Error updating product:', error);
                toast.error('Failed to update product.');
            });
    }

    return (
        <div className="container mt-4">
            <div className="row">
                <div className="col-md-6 text-center">
                    <img 
                        src={image} 
                        alt={title} 
                        className="img-fluid" 
                        style={{ maxHeight: '400px', objectFit: 'contain' }} 
                    />
                </div>
                <div className="col-md-6">
                    <h1 className="mb-3">{title}</h1>
                    <h3 className="text-primary mb-3">Price: ${price}</h3>
                    <p className="mb-4">{description}</p>
                    <p className="mb-4">Quantity: {quantity}</p>
                    <button onClick={Delete} type="button" className="btn btn-danger btn-sm">DELETE</button>
                    <button 
                        type="button" 
                        className="btn btn-warning btn-sm ms-2" 
                        data-bs-toggle="modal" 
                        data-bs-target="#updateProductModal"
                    >
                        Update
                    </button>

                    <div className="mb-3">
                        <h5>Product Details</h5>
                        <ul className="list-unstyled">
                            <li><strong>Product status:</strong> Available</li>
                            <li><strong>Manufacturer:</strong> MUSSA Electronics</li>
                            <li><strong>Model:</strong> MADE IN USA</li>
                        </ul>
                    </div>
                </div>
            </div>

            {/* Related Products Section */}
            <h4 className="mt-5">Related Products</h4>
            <div className="row">
                {relatedProducts.map((product) => (
                    <div key={product.id} className="col-md-3">
                        <div className="card">
                            <img src={product.image} className="card-img-top" alt={product.title} />
                            <div className="card-body">
                                <h5 className="card-title">{product.title}</h5>
                                <p className="card-text">KES {product.price}</p>
                                <button 
                                    className="btn btn-primary btn-sm"
                                    onClick={() => navigate(`/products/${product.id}`)}
                                >
                                    View Details
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Update Product Modal */}
            <div
                className="modal fade"
                id="updateProductModal"
                tabIndex="-1"
                aria-labelledby="updateProductModalLabel"
                aria-hidden="true"
            >
                {/* Modal content here */}
            </div>
        </div>
    );
}

export default ProductDetails;
